mapper.select:

select 

from TSYS_DICT_ITEM
where DICT_ITEM_CODE = #{dictItemCode,jdbcType=VARCHAR}
  and DICT_ENTRY_CODE = #{dictEntryCode,jdbcType=VARCHAR}
  and LANG = #{lang,jdbcType=VARCHAR}

mapper.delete:

delete from TSYS_DICT_ITEM
where DICT_ITEM_CODE = #{dictItemCode,jdbcType=VARCHAR}
  and DICT_ENTRY_CODE = #{dictEntryCode,jdbcType=VARCHAR}
  and LANG = #{lang,jdbcType=VARCHAR}

mapper.update:

update TSYS_DICT_ITEM


    DICT_ITEM_NAME = #{dictItemName,jdbcType=VARCHAR},


    DICT_ITEM_ORDER = #{dictItemOrder,jdbcType=DECIMAL},


    LIFECYCLE = #{lifecycle,jdbcType=VARCHAR},


    PLATFORM = #{platform,jdbcType=VARCHAR},


    REL_CODE = #{relCode,jdbcType=VARCHAR},


where DICT_ITEM_CODE = #{dictItemCode,jdbcType=VARCHAR}
  and DICT_ENTRY_CODE = #{dictEntryCode,jdbcType=VARCHAR}
  and LANG = #{lang,jdbcType=VARCHAR}


update TSYS_DICT_ITEM
set DICT_ITEM_NAME = #{dictItemName,jdbcType=VARCHAR},
  DICT_ITEM_ORDER = #{dictItemOrder,jdbcType=DECIMAL},
  LIFECYCLE = #{lifecycle,jdbcType=VARCHAR},
  PLATFORM = #{platform,jdbcType=VARCHAR},
  REL_CODE = #{relCode,jdbcType=VARCHAR}
where DICT_ITEM_CODE = #{dictItemCode,jdbcType=VARCHAR}
  and DICT_ENTRY_CODE = #{dictEntryCode,jdbcType=VARCHAR}
  and LANG = #{lang,jdbcType=VARCHAR}

